'use strict'

const _ = require('lodash')

// get from raw data, can optionally indicate key
const getRawData = (key) => {
    const json = require('app/json/derivatives.json')
    return _.get(json, key, json)
}


// get filtered data based on raw data
const getFilteredData = (filters) => {
  const data = getRawData('data')

  // filter data based on filters passed in
  return _(data).filter(filters)
}


module.exports = {
  getRawData,
  getFilteredData
}